mapper.select:
SELECT

FROM
  t_receive_address
WHERE
  receive_address_id = #{receiveAddressId}
AND
  open_id = #{userOpenId}
LIMIT 1;

mapper.delete:
DELETE FROM
  t_receive_address
WHERE
  receive_address_id = #{receiveAddressId}
AND
  open_id = #{userOpenId}

mapper.select:
SELECT

FROM
  t_receive_address
WHERE
  open_id = #{userOpenId}
AND
  receive_address_id != #{receiveAddressId}
ORDER BY
  create_time DESC
LIMIT 1;
